        context["domain"] = f"https://{current_domain}/"

        context["DEMO"] = settings.DEMO
        context["demo_username"] = settings.DEMO_USERNAME
        context["demo_password"] = settings.DEMO_PASSWORD

        return context


class PricingView(TemplateView):
    template_name = "index/pricing.html"

    def get_context_data(self, **kwargs) -> dict[str, Any]:
        context = super().get_context_data(**kwargs)
        context["products"] = Product.objects.filter(active=True)

        return context


def get_created_accounts_percentage() -> float:
    days_count = 7
    today = timezone.now()
    start_of_week = today - timedelta(days=days_count)
    prev_start_of_week = start_of_week - timedelta(days=days_count)

    users_created_last_week = User.objects.filter(
        date_joined__range=[start_of_week, today]
    ).count()
    users_created_prev_week = User.objects.filter(
        date_joined__range=[prev_start_of_week, start_of_week]
    ).count()

    percentage_difference = (
        (users_created_last_week - users_created_prev_week)
        / (users_created_last_week + users_created_prev_week)
    ) * 100

    return percentage_difference


def get_formatted_sign(val: float) -> str:
    if val >= 0:
        return "+"
    else:
        return ""  # negative values got minus before value itself


