rtmp {
    server {
        listen 1935;
        chunk_size 4000;

        application live {
            live on;
            hls on;
            hls_nested on;
            hls_fragment 2s;
            hls_playlist_length 360s;
            deny play all;
            hls_path /tmp/hls;
            hls_cleanup on;
            on_publish http://127.0.0.1:8000/api/stream/auth/;
            notify_method post;
            on_publish_done http://127.0.0.1:8000/api/stream/done/;
        }
    }
}

http {
    server {
        listen 80;

