                />
                <FormField
                    control={form.control}
                    name="username"
                    render={({ field }) => (
                        <FormItem>
                            <FormLabel>Username</FormLabel>
                            <FormControl>
                                <Input placeholder="style77" {...field} className="lowercase" />
                            </FormControl>
                            <FormDescription>
                                This is your public display name. You won&apos;t be able to
                                change this later. Remember, your username is your brand, keep it short and sweet!
                            </FormDescription>
                            <FormMessage />
                        </FormItem>
                    )}
                />
                <FormField
                    control={form.control}
                    name="password1"
                    render={({ field }) => (
                        <FormItem>
                            <FormLabel>Password</FormLabel>
                            <FormControl>
                                <Input placeholder="â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢" type="password" {...field} />
                            </FormControl>
                            <FormMessage />
