import React, {Suspense, useRef, useState, useEffect} from "react";
import {Canvas, useFrame, useLoader, extend, useThree} from "@react-three/fiber";

import {SVGLoader} from 'three-stdlib'
import {DoubleSide} from "three/src/constants";

import * as THREE from "three";
import {ExtrudeGeometry, Mesh} from "three";

extend(SVGLoader)
extend(THREE.ExtrudeGeometry)

const loader = new SVGLoader()

export function Shield(props) {
    const ref = useRef<Mesh>()
    const [shape, setShape] = useState<ExtrudeGeometry>()

    useFrame((state, delta) => {
        if (ref.current) ref.current.rotation.y += delta
    })

    useEffect(() => {
        loader.load('/shield.svg', (data) => {
            const paths = data.paths
            const shapes = paths.map((path) => path.toShapes(true))
            const shape = shapes[0]

            const geometry = new THREE.ExtrudeGeometry(shape, {
                depth: 2,
                bevelEnabled: false
            })
            geometry.center()

            setShape(geometry)
        })
    }, [])

    return (
        <mesh {...props} ref={ref} scale={0.1} rotation={[Math.PI, 0, 0]}>
            <meshBasicMaterial depthWrite={false} color="white" side={DoubleSide}/>
            {shape && <primitive object={shape}/>}
        </mesh>
    )
}

import {createContext, useContext, useEffect, useState} from "react";
import {User} from "../types/User";
import {useRouter} from "next/router";

const authContext = createContext(null);

export function ProvideAuth({children}) {
    const auth = useProvideAuth();
    return <authContext.Provider value={auth}>{children}</authContext.Provider>;
}

export const useAuth = () => {
    return useContext(authContext);
}

function useProvideAuth() {
    const [user, setUser] = useState<User | null>(null);

    const signin = async (username: string, password: string) => {
        const params = new URLSearchParams();
        params.append('username', username);
        params.append('password', password);
        params.append('client_secret', password)
        let res = await fetch(`${process.env.API_URL}/api/user/authorize`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/x-www-form-urlencoded',
            },
            body: params,
        });
        if (res.status === 200) {
            let tokens = await res.json();

            res = await fetch(`${process.env.API_URL}/api/user/`, {
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': 'Bearer ' + tokens.access_token,
                }
            })
            if (res.status === 200) {
                let user = await res.json();
                user.accessToken = tokens.access_token;
                user.refreshToken = tokens.refresh_token;
                user.fullName = user.full_name;
                localStorage.setItem('accessToken', tokens.access_token)
                localStorage.setItem('refreshToken', tokens.refresh_token)
                setUser(user);
            }
        }
        return res;
    }

    const signinWithToken = async (accessToken: string, refreshToken: string) => {
        const res = await fetch(`${process.env.API_URL}/api/user/`, {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': 'Bearer ' + accessToken,
            }
        })
        if (res.status === 200) {
            let user = await res.json();
            user.accessToken = accessToken;
            user.refreshToken = refreshToken;
            setUser(user);
        } else {
            const res = await fetch(`${process.env.API_URL}/api/user/refresh?refresh_token=${refreshToken}`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                }
            })
            if (res.status === 200) {
                let tokens = await res.json();
                localStorage.setItem('accessToken', tokens.access_token)
                localStorage.setItem('refreshToken', tokens.refresh_token)
                await signinWithToken(tokens.access_token, tokens.refresh_token);
            } else {
                localStorage.removeItem('accessToken')
                localStorage.removeItem('refreshToken')
            }
        }
    }

    const signup = async (username: string, password: string, email: string, company: string, fullName: string) => {
        const params = new URLSearchParams();
        params.append('username', username);
        params.append('password', password)
        params.append('email', email);
        if (company) params.append('company', company);
        if (fullName) params.append('full_name', fullName);

        return await fetch(`${process.env.API_URL}/api/user/`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                username: username,
                password: password,
                email: email,
                company: company,
                full_name: fullName,
            }),
        });
    }

    const logout = async () => {
        setUser(null);
        localStorage.removeItem('accessToken')
        localStorage.removeItem('refreshToken')
    }


    return {
        user,
        setUser,
        signin,
        signinWithToken,
        signup,
        logout,
    }
}

export function useRequireAuth(redirectUrl = "/login?login_required=true") {
  const auth = useAuth();
  const router = useRouter();

  // If auth.user is false that means we're not
  // logged in and should redirect.
  useEffect(() => {
    if (!auth.user) {

        if (localStorage.getItem('accessToken') && localStorage.getItem('refreshToken')) {
            auth.signinWithToken(localStorage.getItem('accessToken'), localStorage.getItem('refreshToken'));
        } else {
            router.push(redirectUrl);
        }
    }
  }, [auth, router]);

  return auth;
}

import {useEffect} from "react";

export default function useOnClickOutside(ref, handler) {
  useEffect(
    () => {
      const listener = (event) => {
        // Do nothing if clicking ref's element or descendent elements
        if (!ref.current || ref.current.contains(event.target)) {
          return;
        }

        handler(event);
      };

      document.addEventListener("mousedown", listener);
      document.addEventListener("touchstart", listener);

      return () => {
        document.removeEventListener("mousedown", listener);
        document.removeEventListener("touchstart", listener);
      };
    },
    // Add ref and handler to effect dependencies
    // It's worth noting that because passed in handler is a new ...
    // ... function on every render that will cause this effect ...
    // ... callback/cleanup to run every render. It's not a big deal ...
    // ... but to optimize you can wrap handler in useCallback before ...
    // ... passing it into this hook.
    [ref, handler]
  );
}

