    font-weight: 400;
    overflow: hidden;
    max-width: 100%;
  }

  .actmd {
    height: auto;
    display: flex;
  }

  .ipcmd {
    background: inherit;
    color: inherit;
    font-family: inherit;
    padding-right: 100px;
    // width: calc(100% - 120px);
    // max-width: calc();
    position: relative;
    caret-color: transparent;
    margin: 0;

    &::after {
      content: "";
      position: absolute;
      bottom: 0;
      width: 8px;
      height: 2px;
      background: #ccc;
      animation: blink 1s step-end infinite;
    }

    &:active,
    &:focus {
      outline: none;
      border: none;
      caret-color: #ccc;

      &::after {
        display: none;
      }
    }
  }
  @keyframes blink {
    from,
    to {
      background: transparent;
    }

    50% {
      background: #ccc;
    }
  }

  .noteText {
    background: inherit;
    color: var(--txt-col);
    font-family: inherit;
    font-size: 0.88em;
    resize: none;
    width: 100%;
    height: 100%;
    box-sizing: border-box;
    outline: none;
    border: none;
    padding: 4px 8px;

    &::selection {
      background: var(--clrPrm);
      color: var(--wintheme);
    }

    &:active,
    &:focus {
      outline: none;
      border: none;
    }
  }

