import "./general.scss";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import * as FaIcons from "@fortawesome/free-solid-svg-icons";
import * as AllIcons from "./icons";
import {
    IconProp,
} from '@fortawesome/fontawesome-svg-core'

type IconProps = {
    src?: string;

    onClick?: () => void;
    active?: boolean;
    open?: boolean;
    clicked?: boolean;

    className?: string;
    ui?: boolean;  // Use UI icons (from icon/ui)
    fafa?: string;  // FontAwesome Icon
    allIcons?: boolean;  // Use all icons (from icon)
    ext?: boolean;  // Use external icons (from web)

    invert?: boolean;

    width?: number;
    height?: number;
}

type FontAwesomeIconName = keyof typeof FaIcons;
type AllIconsName = keyof typeof AllIcons;

export const Icon = (props: IconProps) => {

    const prtclk = !props.clicked ? "prtclk" : "";

    if (props.fafa) {
        return (
            <div
                className={`uicon ${prtclk} ${props.className || ""}`}
                onClick={props.onClick}
                data-click={props.clicked}
            >
                <FontAwesomeIcon
                    style={{
                        width: props.width,
                        height: props.height || props.width,
                    }}
                    data-invert={props.invert}
                    data-click={props.clicked}
                    icon={
                        FaIcons[props.fafa as FontAwesomeIconName] as IconProp
                    }
                />
            </div>
        );
    } else if (props.allIcons && props.src) {
        const CustomIcon = AllIcons[props.src as AllIconsName];
        return (
            <div
                className={`uicon ${prtclk} ${props.className || ""}`}
                onClick={props.onClick}
            >
                <CustomIcon
                    // @ts-expect-error This is a custom icon component
                    style={{
                        width: props.width || 24,
                        height: props.height || props.width || 24,
                    }} 
                />
            </div>
        );
    } else {
        let src = `icon/${props.ui ? "ui/" : ""}${props.src}.png`;
        if (props.ext != null && props.src && props.src.includes("http")) {
            src = props.src;
        }
        return (
            <div
                className={`uicon ${props.className || ""} ${prtclk}`}
                data-active={props.active}
                data-open={props.open}
                onClick={props.onClick}
            >
                {props.className == "tsIcon" ? (
                    <div
                        style={{ width: props.width, height: props.width }}
                        data-invert={props.invert}
                        data-click={props.clicked}
                    >
                        <img
                            width={props.width}
                            height={props.height}
                            src={src}
                            data-invert={props.invert}
                            data-click={props.clicked}
                        />
                    </div>
                ) : (
                    <img
                        width={props.width}
                        height={props.height}
                        src={src}
                        data-invert={props.invert}
                        data-click={props.clicked}
                    />
                )}
            </div>
        );
    }
};

import { useState, useEffect } from "react";

export const LazyComponent = ({ show, children }: {
    show: boolean;
    children: React.ReactNode;
}) => {
    const [loaded, setLoad] = useState(false);

    useEffect(() => {
        if (show && !loaded) setLoad(true);
    }, [show, loaded]);

    return show || loaded ? <>{children}</> : null;
};

import axios from 'axios';
import fs from 'fs/promises';

const MAX_SIZE = 20000;
const PAT = "";  // Personal Access Token with repo access

async function fetchContents(url) {
  try {
    const response = await axios.get(url, {
      headers: {
        'Authorization': `Bearer ${PAT}`
      }
    });
    const contents = [];

    for (const item of response.data) {
      console.log(`Fetching ${item.path}`)

      const contentItem = {
        name: item.name,
        type: item.type === 'dir' ? 'dir' : 'file',
        path: item.path
      };

      if (item.type === 'dir') {
        const subContents = await fetchContents(item.url);
        contentItem.children = subContents;
      } else {
        console.log(item.name, item.size)
        if (item.size >= MAX_SIZE) {
          console.log(`Skipping ${item.path} because it's too large`);
          continue;
        }

        const fileResponse = await axios.get(item.download_url, {
          headers: {
            'Authorization': `Bearer ${PAT}`
          }
        });
        contentItem.content = fileResponse.data;
      }

      contents.push(contentItem);
    }

    return contents;
  } catch (error) {
    console.error('Error fetching contents:', error);
    return [];
  }
}

async function getAllContents(user, repository) {
  const url = `https://api.github.com/repos/${user}/${repository}/contents/`;
  const allContents = await fetchContents(url);
  return allContents;
}

async function scrapeAndSave(repositories) {
  const scrapedData = {};

  for (const { user, repository } of repositories) {
    console.log(`Scraping ${user}/${repository}`)

    const allContents = await getAllContents(user, repository);
    scrapedData[`${repository}`] = allContents;
  }

  try {
    await fs.writeFile('./src/containers/applications/apps/assets/repos.json', JSON.stringify(scrapedData, null, 2));
    console.log('Scraped data saved to scrapedData.json');
  } catch (error) {
    console.error('Error writing to file:', error);
  }
}

const repositoriesToScrape = [
  { user: 'style77', repository: 'newsltr' },
  { user: 'style77', repository: 'dealscan' },
  { user: 'style77', repository: 'streamx' },
  { user: 'style77', repository: 'insightguard' },
  { user: 'style77', repository: 'quantex' },
  { user: 'style77', repository: 'joachimhodana.com' },
  { user: 'style77', repository: 'darkangel'}
];

scrapeAndSave(repositoriesToScrape);

